from PIL import Image

def bitstream_to_image(bitstream, width, height):
    # Create a new image with the specified width and height
    image = Image.new("1", (width, height), color=0)

    # Convert bitstream to a list of integers
    pixels = [int(bit) for bit in bitstream]

    # Set the pixels in the image
    image.putdata(pixels)

    return image

# Example bitstream (replace this with your actual bitstream)
bitstream1 = ""
bitstream2 = ""
print(len(bitstream1))
print(len(bitstream2))
# Specify the width and height of the image
width = 400
height = 20

# Convert bitstream to image
image1 = bitstream_to_image(bitstream1, width, height)
image2 = bitstream_to_image(bitstream2, width, height)
# Save the image to a file (optional)
image1.save("flag0.bmp")
image2.save("flag32.bmp")
# Show the image (optional)
image1.show()
image2.show()
